import React, { useState, useEffect, useRef } from 'react';
import './App.css';
import { 
  FiClock, FiCoffee, FiActivity, FiAlertCircle, FiCheckCircle, 
  FiPause, FiPlay, FiSettings, FiVolume2, FiVolumeX, FiX 
} from 'react-icons/fi';
import notificationSound from './notification.mp3';

function App() {
  const [screenTime, setScreenTime] = useState(0);
  const [breaks, setBreaks] = useState(0);
  const [lastBreak, setLastBreak] = useState(0);
  const [isTracking, setIsTracking] = useState(false);
  const [isPaused, setIsPaused] = useState(false);
  const [showSettings, setShowSettings] = useState(false);
  const [breakTimer, setBreakTimer] = useState(0);
  const [isOnBreak, setIsOnBreak] = useState(false);
  const audioRef = useRef(new Audio(notificationSound));

  const [settings, setSettings] = useState({
    maxScreenTime: 120, // 2 hours in minutes
    breakInterval: 30,  // remind for break every 30 minutes
    breakDuration: 5,   // 5 minutes break
    notificationsEnabled: true,
    soundEnabled: true,
  });

  // Request notification permission on mount
  useEffect(() => {
    if ("Notification" in window) {
      Notification.requestPermission();
    }
  }, []);

  // Screen time tracking
  useEffect(() => {
    let timer;
    if (isTracking && !isPaused && !isOnBreak) {
      timer = setInterval(() => {
        setScreenTime(prev => {
          const newTime = prev + 1;
          // Check if we need to show break notification
          if (newTime % settings.breakInterval === 0) {
            showNotification(
              "Time for a break!", 
              `You've been working for ${settings.breakInterval} minutes. Take a ${settings.breakDuration} minute break!`,
              true
            );
          }
          // Check if max screen time reached
          if (newTime >= settings.maxScreenTime) {
            showNotification(
              "Maximum Screen Time Reached!", 
              "You've reached your maximum screen time limit. Consider taking a longer break.",
              true
            );
          }
          return newTime;
        });
        setLastBreak(prev => prev + 1);
      }, 60000); // Update every minute
    }
    return () => clearInterval(timer);
  }, [isTracking, isPaused, settings, isOnBreak]);

  // Break timer
  useEffect(() => {
    let timer;
    if (isOnBreak) {
      timer = setInterval(() => {
        setBreakTimer(prev => {
          if (prev <= 1) {
            showNotification(
              "Break Time Over!", 
              "Time to get back to work. Remember to maintain good posture!",
              true
            );
            setIsOnBreak(false);
            setIsPaused(false);
            return 0;
          }
          return prev - 1;
        });
      }, 60000); // Update every minute
    }
    return () => clearInterval(timer);
  }, [isOnBreak]);

  // Document visibility change detection
  useEffect(() => {
    const handleVisibilityChange = () => {
      if (document.hidden && isTracking && !isOnBreak) {
        setIsPaused(true);
        showNotification(
          "Break Detected", 
          "Timer paused. Enjoy your break!",
          false
        );
        setBreaks(prev => prev + 1);
        setLastBreak(0);
      }
    };

    document.addEventListener("visibilitychange", handleVisibilityChange);
    return () => document.removeEventListener("visibilitychange", handleVisibilityChange);
  }, [isTracking, isOnBreak]);

  const showNotification = (title, body, playSound = false) => {
    if (settings.notificationsEnabled && "Notification" in window && Notification.permission === "granted") {
      new Notification(title, { body });
    }
    if (settings.soundEnabled && playSound) {
      audioRef.current.play().catch(e => console.log('Audio play failed:', e));
    }
  };

  const toggleTracking = () => {
    if (!isTracking) {
      setIsTracking(true);
      setIsPaused(false);
      showNotification(
        "Tracking Started", 
        "Screen time tracking has begun.",
        false
      );
    } else {
      setIsTracking(false);
      setIsPaused(false);
      showNotification(
        "Tracking Stopped", 
        "Screen time tracking has stopped.",
        false
      );
    }
  };

  const startBreak = () => {
    setIsOnBreak(true);
    setIsPaused(true);
    setBreakTimer(settings.breakDuration);
    setBreaks(prev => prev + 1);
    setLastBreak(0);
    showNotification(
      "Break Started", 
      `Taking a ${settings.breakDuration} minute break. Relax and stretch!`,
      true
    );
  };

  const skipBreak = () => {
    setIsOnBreak(false);
    setIsPaused(false);
    setBreakTimer(0);
  };

  const formatTime = (minutes) => {
    const hours = Math.floor(minutes / 60);
    const mins = minutes % 60;
    return `${hours}h ${mins}m`;
  };

  const getRiskLevel = () => {
    if (screenTime >= settings.maxScreenTime) return 'high';
    if (screenTime >= settings.maxScreenTime * 0.7) return 'medium';
    return 'low';
  };

  const getRecommendations = () => {
    if (isOnBreak) {
      return [
        `${breakTimer} minutes of break time remaining`,
        "Stand up and stretch",
        "Look at something 20 feet away for 20 seconds",
        "Take deep breaths"
      ];
    }

    const risk = getRiskLevel();
    if (risk === 'high') {
      return [
        "Please take a long break now",
        "Stand up and stretch",
        "Consider ending your session",
        "Get some fresh air"
      ];
    }
    if (risk === 'medium') {
      return [
        "Consider taking a short break",
        "Do some quick stretches",
        "Drink some water",
        "Rest your eyes for a few minutes"
      ];
    }
    return [
      "You're doing great!",
      "Keep maintaining healthy breaks",
      "Stay hydrated"
    ];
  };

  return (
    <div className="app">
      <header className="app-header">
        <h1>Tech Breaks</h1>
        <p>Automatic Screen Time Monitor</p>
        <button 
          className="settings-button"
          onClick={() => setShowSettings(!showSettings)}
        >
          <FiSettings />
        </button>
      </header>

      {showSettings && (
        <div className="settings-modal">
          <div className="settings-content">
            <div className="settings-header">
              <h2>Settings</h2>
              <button onClick={() => setShowSettings(false)}><FiX /></button>
            </div>
            <div className="settings-body">
              <div className="setting-item">
                <label>Maximum Screen Time (minutes)</label>
                <input
                  type="number"
                  value={settings.maxScreenTime}
                  onChange={(e) => setSettings(prev => ({
                    ...prev,
                    maxScreenTime: parseInt(e.target.value)
                  }))}
                  min="30"
                />
              </div>
              <div className="setting-item">
                <label>Break Interval (minutes)</label>
                <input
                  type="number"
                  value={settings.breakInterval}
                  onChange={(e) => setSettings(prev => ({
                    ...prev,
                    breakInterval: parseInt(e.target.value)
                  }))}
                  min="15"
                />
              </div>
              <div className="setting-item">
                <label>Break Duration (minutes)</label>
                <input
                  type="number"
                  value={settings.breakDuration}
                  onChange={(e) => setSettings(prev => ({
                    ...prev,
                    breakDuration: parseInt(e.target.value)
                  }))}
                  min="1"
                />
              </div>
              <div className="setting-item">
                <label>
                  <input
                    type="checkbox"
                    checked={settings.notificationsEnabled}
                    onChange={(e) => setSettings(prev => ({
                      ...prev,
                      notificationsEnabled: e.target.checked
                    }))}
                  />
                  Enable Notifications
                </label>
              </div>
              <div className="setting-item">
                <label>
                  <input
                    type="checkbox"
                    checked={settings.soundEnabled}
                    onChange={(e) => setSettings(prev => ({
                      ...prev,
                      soundEnabled: e.target.checked
                    }))}
                  />
                  Enable Sound
                </label>
              </div>
            </div>
          </div>
        </div>
      )}

      <main className="app-main">
        <section className="tracking-section">
          <div className="tracking-stats">
            <div className="stat-card">
              <FiClock className="stat-icon" />
              <div className="stat-info">
                <label>Screen Time</label>
                <span className="stat-value">{formatTime(screenTime)}</span>
              </div>
            </div>

            <div className="stat-card">
              <FiCoffee className="stat-icon" />
              <div className="stat-info">
                <label>Breaks Taken</label>
                <span className="stat-value">{breaks}</span>
              </div>
            </div>

            <div className="stat-card">
              <FiActivity className="stat-icon" />
              <div className="stat-info">
                <label>Last Break</label>
                <span className="stat-value">{formatTime(lastBreak)} ago</span>
              </div>
            </div>
          </div>

          <div className="action-buttons">
            <button 
              className={`tracking-button ${isTracking ? 'active' : ''}`}
              onClick={toggleTracking}
              disabled={isOnBreak}
            >
              {isTracking ? <FiPause /> : <FiPlay />}
              {isTracking ? 'Stop Tracking' : 'Start Tracking'}
            </button>

            {isTracking && !isOnBreak && (
              <button 
                className="break-button"
                onClick={startBreak}
              >
                <FiCoffee />
                Take a Break
              </button>
            )}

            {isOnBreak && (
              <button 
                className="skip-button"
                onClick={skipBreak}
              >
                Skip Break
              </button>
            )}
          </div>
        </section>

        <section className="status-section">
          <div className={`status-card ${getRiskLevel()}`}>
            <h2>Current Status</h2>
            <div className="status-info">
              <p className="status-level">
                {getRiskLevel() === 'low' && <FiCheckCircle />}
                {getRiskLevel() === 'medium' && <FiAlertCircle />}
                {getRiskLevel() === 'high' && <FiAlertCircle className="pulse" />}
                {isOnBreak ? 'On Break' : `${getRiskLevel().charAt(0).toUpperCase() + getRiskLevel().slice(1)} Risk`}
              </p>
              <div className="recommendations">
                <h3>Recommendations</h3>
                <ul>
                  {getRecommendations().map((rec, index) => (
                    <li key={index}>{rec}</li>
                  ))}
                </ul>
              </div>
            </div>
          </div>
        </section>
      </main>

      <footer className="app-footer">
        <p>Take care of your digital wellbeing ❤️</p>
        <button 
          className="sound-toggle"
          onClick={() => setSettings(prev => ({
            ...prev,
            soundEnabled: !prev.soundEnabled
          }))}
        >
          {settings.soundEnabled ? <FiVolume2 /> : <FiVolumeX />}
        </button>
      </footer>
    </div>
  );
}

export default App;
