import React, { useState, useEffect } from 'react';
import './App.css';
import { FiClock, FiCoffee, FiActivity, FiAlertCircle, FiCheckCircle, FiPause, FiPlay } from 'react-icons/fi';

function App() {
  const [screenTime, setScreenTime] = useState(0);
  const [breaks, setBreaks] = useState(0);
  const [lastBreak, setLastBreak] = useState(0);
  const [isTracking, setIsTracking] = useState(false);
  const [isPaused, setIsPaused] = useState(false);
  const [settings, setSettings] = useState({
    maxScreenTime: 120, // 2 hours in minutes
    breakInterval: 30,  // remind for break every 30 minutes
    notificationsEnabled: true
  });

  // Request notification permission on mount
  useEffect(() => {
    if ("Notification" in window) {
      Notification.requestPermission();
    }
  }, []);

  // Screen time tracking
  useEffect(() => {
    let timer;
    if (isTracking && !isPaused) {
      timer = setInterval(() => {
        setScreenTime(prev => {
          const newTime = prev + 1;
          // Check if we need to show break notification
          if (newTime % settings.breakInterval === 0) {
            showNotification("Time for a break!", 
              `You've been working for ${settings.breakInterval} minutes. Take a short break!`);
          }
          // Check if max screen time reached
          if (newTime >= settings.maxScreenTime) {
            showNotification("Maximum Screen Time Reached!", 
              "You've reached your maximum screen time limit. Consider taking a longer break.");
          }
          return newTime;
        });
        setLastBreak(prev => prev + 1);
      }, 60000); // Update every minute
    }
    return () => clearInterval(timer);
  }, [isTracking, isPaused, settings]);

  // Document visibility change detection
  useEffect(() => {
    const handleVisibilityChange = () => {
      if (document.hidden && isTracking) {
        setIsPaused(true);
        const breakTime = new Date();
        showNotification("Break Detected", "Timer paused. Enjoy your break!");
        setBreaks(prev => prev + 1);
        setLastBreak(0);
      }
    };

    document.addEventListener("visibilitychange", handleVisibilityChange);
    return () => document.removeEventListener("visibilitychange", handleVisibilityChange);
  }, [isTracking]);

  const showNotification = (title, body) => {
    if (settings.notificationsEnabled && "Notification" in window && Notification.permission === "granted") {
      new Notification(title, { body });
    }
  };

  const toggleTracking = () => {
    if (!isTracking) {
      setIsTracking(true);
      setIsPaused(false);
      showNotification("Tracking Started", "Screen time tracking has begun.");
    } else {
      setIsTracking(false);
      setIsPaused(false);
      showNotification("Tracking Stopped", "Screen time tracking has stopped.");
    }
  };

  const formatTime = (minutes) => {
    const hours = Math.floor(minutes / 60);
    const mins = minutes % 60;
    return `${hours}h ${mins}m`;
  };

  const getRiskLevel = () => {
    if (screenTime >= settings.maxScreenTime) return 'high';
    if (screenTime >= settings.maxScreenTime * 0.7) return 'medium';
    return 'low';
  };

  const getRecommendations = () => {
    const risk = getRiskLevel();
    if (risk === 'high') {
      return [
        "Please take a long break now",
        "Stand up and stretch",
        "Consider ending your session",
        "Get some fresh air"
      ];
    }
    if (risk === 'medium') {
      return [
        "Consider taking a short break",
        "Do some quick stretches",
        "Drink some water",
        "Rest your eyes for a few minutes"
      ];
    }
    return [
      "You're doing great!",
      "Keep maintaining healthy breaks",
      "Stay hydrated"
    ];
  };

  return (
    <div className="app">
      <header className="app-header">
        <h1>Tech Breaks</h1>
        <p>Automatic Screen Time Monitor</p>
      </header>

      <main className="app-main">
        <section className="tracking-section">
          <div className="tracking-stats">
            <div className="stat-card">
              <FiClock className="stat-icon" />
              <div className="stat-info">
                <label>Screen Time</label>
                <span className="stat-value">{formatTime(screenTime)}</span>
              </div>
            </div>

            <div className="stat-card">
              <FiCoffee className="stat-icon" />
              <div className="stat-info">
                <label>Breaks Taken</label>
                <span className="stat-value">{breaks}</span>
              </div>
            </div>

            <div className="stat-card">
              <FiActivity className="stat-icon" />
              <div className="stat-info">
                <label>Last Break</label>
                <span className="stat-value">{formatTime(lastBreak)} ago</span>
              </div>
            </div>
          </div>

          <button 
            className={`tracking-button ${isTracking ? 'active' : ''}`}
            onClick={toggleTracking}
          >
            {isTracking ? <FiPause /> : <FiPlay />}
            {isTracking ? 'Stop Tracking' : 'Start Tracking'}
          </button>
        </section>

        <section className="status-section">
          <div 
            className={`status-card ${getRiskLevel()}`}
          >
            <h2>Current Status</h2>
            <div className="status-info">
              <p className="status-level">
                {getRiskLevel() === 'low' && <FiCheckCircle />}
                {getRiskLevel() === 'medium' && <FiAlertCircle />}
                {getRiskLevel() === 'high' && <FiAlertCircle className="pulse" />}
                {getRiskLevel().charAt(0).toUpperCase() + getRiskLevel().slice(1)} Risk
              </p>
              <div className="recommendations">
                <h3>Recommendations</h3>
                <ul>
                  {getRecommendations().map((rec, index) => (
                    <li key={index}>{rec}</li>
                  ))}
                </ul>
              </div>
            </div>
          </div>
        </section>
      </main>

      <footer className="app-footer">
        <p>Take care of your digital wellbeing ❤️</p>
      </footer>
    </div>
  );
}

export default App;
