import React, { useState } from 'react';

const burnoutTips = [
  "Take regular short breaks from screens.",
  "Get at least 7-8 hours of sleep each night.",
  "Practice mindfulness or meditation.",
  "Stay hydrated and eat balanced meals.",
  "Talk to someone you trust about how you’re feeling.",
  "Limit multitasking and focus on one thing at a time.",
  "Schedule time for hobbies and relaxation.",
  "Exercise regularly, even a short walk helps."
];

export default function Home() {
  const [mood, setMood] = useState(3);
  const [sleep, setSleep] = useState(7);
  const [burnoutRisk, setBurnoutRisk] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Assume screen time is in localStorage (in seconds)
  const screenTime = parseInt(localStorage.getItem('screenTime') || '0', 10) / 60; // minutes

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);
    setBurnoutRisk(null);
    try {
      const response = await fetch('/ml-service/predict-burnout', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ mood, screen_time: screenTime, sleep }),
      });
      if (!response.ok) throw new Error('Prediction failed');
      const data = await response.json();
      setBurnoutRisk(data.burnout_risk);
    } catch (err) {
      setError('Could not get burnout prediction.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h1>Welcome to Calm Pulse</h1>
      <p>Track your screen time, take healthy breaks, and improve your well-being.</p>

      <div className="burnout-check-card" style={{margin: '2rem 0', padding: '1.5rem', background: '#f0fdfa', borderRadius: '1rem', maxWidth: 400}}>
        <h2>Burnout Risk Check</h2>
        <form onSubmit={handleSubmit} style={{display: 'flex', flexDirection: 'column', gap: 14}}>
          <label>
            Mood (1 = Low, 5 = Great):
            <input
              type="number"
              min="1"
              max="5"
              value={mood}
              onChange={e => setMood(Number(e.target.value))}
              required
              style={{marginLeft: 10, marginBottom: 10, width: 60}}
            />
          </label>
          <label>
            Sleep hours (last night):
            <input
              type="number"
              min="0"
              max="24"
              value={sleep}
              onChange={e => setSleep(Number(e.target.value))}
              required
              style={{marginLeft: 10, marginBottom: 10, width: 60}}
            />
          </label>
          <button type="submit" disabled={loading} style={{marginTop: 10, width: 180, alignSelf: 'center'}}>
            {loading ? 'Checking...' : 'Check Burnout Risk'}
          </button>
        </form>
        {burnoutRisk && (
          <div style={{marginTop: 18, fontWeight: 'bold', color: burnoutRisk === 'High' ? '#e11d48' : burnoutRisk === 'Medium' ? '#f59e42' : '#0ea5e9'}}>
            Burnout Risk: {burnoutRisk}
            {burnoutRisk === 'High' && (
              <div style={{marginTop: 12, background: '#fee2e2', color: '#b91c1c', borderRadius: 8, padding: '10px 14px', fontWeight: 500}}>
                <div style={{marginBottom: 6}}>Tips to Reduce Burnout:</div>
                <ul style={{margin: 0, paddingLeft: 18}}>
                  {burnoutTips.slice(0, 4).map((tip, i) => (
                    <li key={i}>{tip}</li>
                  ))}
                </ul>
              </div>
            )}
          </div>
        )}
        {error && <div style={{color: 'red', marginTop: 10}}>{error}</div>}
      </div>
      {/* Dashboard widgets will go here */}
    </div>
  );
}
